import classnames from 'classnames';
import { COLOR } from '../../../constant/color';
import { Size } from '../../../constant/size';
import { SvgIcon } from '../svg/SvgIcon';

type BadgeColor = 'gray' | 'error' | 'warning' | 'success' | 'pink' | 'info' | 'yellow' | 'violet';

interface BadgeProps {
  className?: string;
  color?: BadgeColor;
  label: string;
  type?: 'pill' | 'badge';
  onlyOutline?: boolean;
  size?: Omit<Size, 'xl' | '2xl'>;
  icon?: 'dot' | 'x' | 'arrowRight';
}

const BADGE_COLOR: { [key: string]: 'blue' | 'green' | 'orange' | 'red' } = {
  info: 'blue',
  success: 'green',
  warning: 'orange',
  error: 'red',
};

export const Badge = ({
  icon,
  size = 'md',
  type = 'pill',
  onlyOutline = false,
  className,
  color = 'violet',
  label,
}: BadgeProps) => {
  const svgColor = `${BADGE_COLOR[color] || color}600` as keyof typeof COLOR;

  return (
    <span
      className={classnames(
        'badge',
        `badge--${color}`,
        `badge--${size}`,
        `badge--${type}`,
        onlyOutline ? `badge--outline` : '',
        icon === 'dot' ? 'badge--dot' : '',
        label,
        className
      )}
    >
      <span className="badge__label">{label}</span>
      {icon === 'x' && <SvgIcon icon="xClose" size={16} color={svgColor} />}
      {icon === 'arrowRight' && <SvgIcon icon="arrowRight" size={16} color={svgColor} />}
    </span>
  );
};

interface BadgeGroupProps extends Omit<BadgeProps, 'size'> {
  href?: string;
  size?: Omit<Size, 'sm'>;
  leading?: Omit<BadgeProps, 'className' | 'size' | 'color' | 'type'>;
  trailing?: {
    href: string;
    label: string;
  };
}

export const BadgeGroup = ({
  href,
  leading,
  color = 'violet',
  type = 'pill',
  trailing,
  size = 'md',
  label,
  className,
  ...props
}: BadgeGroupProps) => {
  return (
    <span
      className={classnames(
        'badge-group',
        `badge-group--${color}`,
        `badge-group--${type}`,
        `badge-group--${size}`,
        className
      )}
      {...props}
    >
      {leading && (
        <Badge
          label={leading.label}
          type={type}
          size="sm"
          color={color}
          icon={leading.icon}
          onlyOutline={leading.onlyOutline}
        />
      )}
      {!trailing && (
        <a className="badge-group__link" href={href}>
          <span>{label} </span>
          <SvgIcon icon="arrowRight" size={16} color={`${BADGE_COLOR[color] || color}600` as keyof typeof COLOR} />
        </a>
      )}
      {trailing && (
        <a href={trailing.href}>
          <Badge label={trailing.label} type={type} size="sm" color={color} icon="arrowRight" />
        </a>
      )}
    </span>
  );
};
